import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import React, { useState, useEffect, useContext } from 'react';
import Skills from './pages/Skills';
import JobsTable from './pages/JobTable';
import JobLocation from './pages/JobLocation';
import JobCat from './pages/JobCat';
import JobApp from './pages/JobApp';
import NewJob from './pages/NewJob';

function App() {
  const [dataSource, setDataSource] = useState([
    {
      key: '0',
      id: '0',
      title: '32',
      location: 'London',
      skills:'skill1',
      Sdate: '2021-02-23',
      Edate: '2023-02-23',
      status: 'Yes',
    },
    {
      key: '1',
      id: ' 1',
      title: '33',
      location: 'New York',
      Sdate: '2020-03-23',
      Edate: '2022-02-23',
      status: 'Yes',
    },
    {
      key: '2',
      id: '2',
      title: '34',
      location: 'Nablus',
      Sdate: '2019-02-25',
      Edate: '2019-10-23',
      status: 'No',
    },
    {
      key: '3',
      id: '3',
      title: '35',
      location: 'London',
      Sdate: '2021-02-23',
      Edate: '2023-02-23',
      status: 'Yes',
    },
    {
      key: '4',
      id: '4',
      title: '36',
      location: 'Palestine',
      Sdate: '2020-06-23',
      Edate: '2020-12-23',
      status: 'No',
    },
  ]);
  const [count, setCount] = useState(5);
  const [input, setInput] = useState();
  const [ModalVisible, setModalVisible] = useState(false);
  const [status, setStatus] = useState();
  const [location, setLocation] = useState();
  const [jobLocation, setJobLocation] = useState();
  const [jobName, setJobName] = useState('');
  const [skills, setSkills] = useState();
  const [skillName, setSkillName] = useState();
  const [startDate, setStartDate] = useState();
  const [EndDate, setEndDate] = useState();
  const [result, setResult] = useState([]);
  const [visible2, setVisible2] = useState(false);

  function onChangeStart(date, dateString) {
    console.log(date, dateString);
    setStartDate(dateString);
  }

  function onChangeEnd(date, dateString) {
    console.log(date, dateString);
    setEndDate(dateString);
  }

  function handleChanges(newValue) {
    setResult(newValue);
  }

  useEffect(() => {
    setResult(dataSource);
  }, []);
  const onChangeStatus = (value) => {
    setStatus(value);
  };

  const onChangeLocation = (value) => {
    setLocation(value);
  };

  const onChangeS = (value) => {
    console.log(value);
    const results = !value
      ? dataSource
      : dataSource.filter((item) =>
          item.location.toLowerCase().includes(value.toLowerCase())
        );

    if (value === 'all') {
      setResult(dataSource);
    } else {
      setInput(value);
      setResult(results);
    }
    console.log(results);
    console.log(result);
  };

  const handleDelete = (key) => {
    const dataSource2 = [...result];
    setResult(dataSource2.filter((item) => item.key !== key));
  };

  const onSearch = (value) => {
    console.log(value);
    const result = !value
      ? dataSource
      : dataSource.filter(
          (item) =>
            item.location.toLowerCase().includes(value.toLocaleLowerCase()) ||
            item.title.toLowerCase().includes(value.toLocaleLowerCase()) ||
            item.status.toLowerCase().includes(value.toLocaleLowerCase())
        );
    setResult(result);
  };

  const showModal = () => {
    setModalVisible(true);
  };
  /*2020-03-23  2022-02-23*/
  const handleOk = () => {
    const result = ''
      ? dataSource
      : dataSource.filter(
          (item) =>
            item.location
              .toLocaleLowerCase()
              .includes(location.toLocaleLowerCase()) &&
            item.status
              .toLocaleLowerCase()
              .includes(status.toLocaleLowerCase()) &&
            item.Sdate > startDate &&
            item.Edate <= EndDate
        );
    setModalVisible(false);
    setResult(result);
  };

  const handleCancel = () => {
    setModalVisible(false);
    setStatus('');
    setLocation('');
  };

  const SaveSelectLocation = (value) => {
    setJobLocation(value);
  };

  const handleChangeLocation = (event) => {
    setJobName(event.target.value);
  };

  const handleOkAddJob = () => {
    const count2 = count + 1;
    setResult((result) => [
      ...result,
      {
        title: jobName,
        location: jobLocation,
        key: count2,
        id: count2,
      },
    ]);
    setCount(count2);
    console.log(count);
    setVisible2(false);
  };

  const showModalAddJob = () => {
    setVisible2(true);
  };

  const handleCancelAddJob = () => {
    setVisible2(false);
    // setJobName('');
    // setJobLocation('');
  };

  const SaveSelectSkills = (value) => {
    setSkills(value);
  };

  const handleChangeSkills = (event) => {
    setSkillName(event.target.value);
  };

  const handleOkAddSkills = () => {
    const count2 = count + 1;
    setResult((result) => [
      ...result,
      {
        title: skillName,
        Skills: Skills,
        key: count2,
        id: count2,
      },
    ]);
    setCount(count2);
    console.log(count);
    setVisible2(false);
  };
  return (
    <>
      <Router>
        <div className="App">
          <div className="content">
            <Switch>
              <Route exact path="/" component={null} />
              <Route exact path="/JobTable">
                <JobsTable
                  onChangeS={onChangeS}
                  result={result}
                  handleDelete={handleDelete}
                  setResult={setResult}
                  onChange={handleChanges}
                />
              </Route>
              <Route exact path="/JobCategories">
                <JobCat
                  onChangeS={onChangeS}
                  result={result}
                  handleDelete={handleDelete}
                />
              </Route>
              <Route exact path="/JobLocations">
                <JobLocation
                  onChangeS={onChangeS}
                  result={result}
                  handleDelete={handleDelete}
                  SaveSelectLocation={SaveSelectLocation}
                  jobLocation={jobLocation}
                  handleChangeLocation={handleChangeLocation}
                  handleOkAddJob={handleOkAddJob}
                  handleCancelAddJob={handleCancelAddJob}
                  showModalAddJob={showModalAddJob}
                  visible2={visible2}
                />
              </Route>
              <Route exact path="/Skills">
                <Skills
                  onChangeS={onChangeS}
                  result={result}
                  handleDelete={handleDelete}
                  handleOkAddJob={handleOkAddJob}
                  handleCancelAddJob={handleCancelAddJob}
                  showModalAddJob={showModalAddJob}
                  visible2={visible2}
                  SaveSelectSkills={SaveSelectSkills}
                  SkillName={skillName}
                  handleChangeSkills={handleChangeSkills}
                  handleOkAddSkills={handleOkAddSkills}
                />
              </Route>
              <Route exact path="/JobApplications">
                <JobApp
                  onSearch={onSearch}
                  result={result}
                  handleDelete={handleDelete}
                  showModal={showModal}
                  handleOk={handleOk}
                  handleCancel={handleCancel}
                  ModalVisible={ModalVisible}
                  onChangeStatus={onChangeStatus}
                  onChangeLocation={onChangeLocation}
                  onChangeStart={onChangeStart}
                  onChangeEnd={onChangeEnd}
                />
              </Route>
              <Route exact path="/NewJob" component={NewJob} />
            </Switch>
          </div>
        </div>
      </Router>
    </>
  );
}

export default App;
