import { Form, Input, Button } from 'antd';
import './style-jobApply.css';

function ApplyForm(props) {
  const layout = {
    labelCol: {
      span: 2,
    },
    wrapperCol: {
      span: 16,
    },
  };
  /* eslint-disable no-template-curly-in-string */

  const validateMessages = {
    required: '${label} is required!',
    types: {
      email: '${label} is not a valid email!',
      number: '${label} is not a valid number!',
    },
    number: {
      range: '${label} must be between ${min} and ${max}',
    },
  };
  const onFinish = (values) => {
    console.log(values);
  };
  return (
    <>
      <Form
        {...layout}
        name="nest-messages"
        onFinish={onFinish}
        validateMessages={validateMessages}
        style={{
          paddingTop: `57px`,
        }}
      >
        <Form.Item
          name={['user', 'name']}
          label="Full Name:"
          labelCol={{ ...layout.labelCol, offset: 3 }}
        >
          <Input className="applyF"/>
        </Form.Item>
        <Form.Item
          name={['user', 'age']}
          label="Date of birth:"
          labelCol={{ ...layout.labelCol, offset: 3 }}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name={['user', 'website']}
          label="Location:"
          labelCol={{ ...layout.labelCol, offset: 3 }}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name={['user', 'website']}
          label="Upload CV:"
          labelCol={{ ...layout.labelCol, offset: 3 }}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name={['user', 'introduction']}
          label="More info:"
          labelCol={{ ...layout.labelCol, offset: 3 }}
        >
          <Input.TextArea />
        </Form.Item>
        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 5 }}>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    </>
  );
}

export default ApplyForm;
