import React, { useContext, useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import { Table, Input, Button, Popconfirm, Form, Space } from 'antd';
import './style.css';
import edit from '../../images/edit.svg';
import deletee from '../../images/delete.svg';

const EditableContext = React.createContext(null);

const EditableRow = ({ index, ...props }) => {
  const [form] = Form.useForm();
  return (
    <Form form={form} component={false}>
      <EditableContext.Provider value={form}>
        <tr {...props} />
      </EditableContext.Provider>
    </Form>
  );
};

const EditableCell = ({
  title,
  editable,
  children,
  dataIndex,
  record,
  handleSave,
  ...restProps
}) => {
  const [editing, setEditing] = useState(false);
  const inputRef = useRef(null);
  const form = useContext(EditableContext);
  useEffect(() => {
    if (editing) {
      inputRef.current.focus();
    }
  }, [editing]);

  const toggleEdit = () => {
    setEditing(!editing);
    form.setFieldsValue({
      [dataIndex]: record[dataIndex],
    });
  };

  const save = async () => {
    try {
      const values = await form.validateFields();
      toggleEdit();
      handleSave({ ...record, ...values });
    } catch (errInfo) {
      console.log('Save failed:', errInfo);
    }
  };

  let childNode = children;

  if (editable) {
    childNode = editing ? (
      <Form.Item
        style={{
          margin: 0,
        }}
        name={dataIndex}
        rules={[
          {
            required: true,
            message: `${title} is required.`,
          },
        ]}
      >
        <Input ref={inputRef} onPressEnter={save} onBlur={save} />
      </Form.Item>
    ) : (
      <div
        className="editable-cell-value-wrap"
        style={{
          paddingRight: 24,
        }}
        onClick={toggleEdit}
      >
        {children}
      </div>
    );
  }

  return <td {...restProps}>{childNode}</td>;
};

function onChange(pagination, filters, sorter, extra) {
  console.log('params', pagination, filters, sorter, extra);
}

class EditableTable extends React.Component {
  constructor(props) {
    super(props);
    this.columns = [
      {
        title: '#',
        dataIndex: 'id',
        width: '5%',
        // editable: true,
        filters: [
          {
            text: '0',
            value: '0',
          },
          {
            text: '1',
            value: '1',
          },
        ],
        filterMultiple: false,
        onFilter: (value, record) => record.address.indexOf(value) === 0,
        sorter: (a, b) => a.address.length - b.address.length,
        sortDirections: ['descend', 'ascend'],
      },
      {
        title: 'JOB TITLE',
        dataIndex: 'title',
      },
      {
        title: 'JOB LOCATION',
        dataIndex: 'location',
      },
      {
        title: 'START DATE',
        dataIndex: 'Sdate',
      },
      {
        title: 'END DATE',
        dataIndex: 'Edate',
      },
      {
        title: 'STATUS',
        dataIndex: 'status',
      },
      {
        title: '',
        dataIndex: 'operation',
        width: '20%',
        render: (_, record) => (
          <Space size="middle" style={{ cursor: 'pointer' }}>
            {/* <a>Edit</a> */}
            <img src={edit} alt="edit" />
            <Popconfirm
              title="Sure to delete?"
              onConfirm={() => this.handleDelete(record.key)}
            >
              {/* <a>Delete</a> */}
              <img src={deletee} alt="delete" />
            </Popconfirm>
          </Space>
        ),
      },
    ];
    this.state = {
      dataSource: [
        {
          key: '0',
          id: '0',
          title: '32',
          location: 'London, Park Lane no. 0',
          Sdate: '5-10',
          Edate: '9-10',
          status: 'yes',
        },
        {
          key: '1',
          id: ' 1',
          title: '32',
          location: 'London, Park Lane no. 1',
          Sdate: '5-10',
          Edate: '9-10',
          status: 'yes',
        },
      ],
      count: 2,
    };
  }

  handleDelete = (key) => {
    const dataSource = [...this.state.dataSource];
    this.setState({
      dataSource: dataSource.filter((item) => item.key !== key),
    });
  };
  handleAdd = () => {
    const { count, dataSource } = this.state;
    const newData = {
      key: count,
      id: `${count}`,
      title: '32',
      location: `London, Park Lane no. ${count}`,
      Sdate: '5-10',
      Edate: '9-10',
      status: 'yes',
    };
    this.setState({
      dataSource: [...dataSource, newData],
      count: count + 1,
    });
  };
  handleSave = (row) => {
    const newData = [...this.state.dataSource];
    const index = newData.findIndex((item) => row.key === item.key);
    const item = newData[index];
    newData.splice(index, 1, { ...item, ...row });
    this.setState({
      dataSource: newData,
    });
  };

  render() {
    const { dataSource } = this.state;
    const components = {
      body: {
        row: EditableRow,
        cell: EditableCell,
      },
    };
    const columns = this.columns.map((col) => {
      if (!col.editable) {
        return col;
      }

      return {
        ...col,
        onCell: (record) => ({
          record,
          editable: col.editable,
          dataIndex: col.dataIndex,
          title: col.title,
          handleSave: this.handleSave,
        }),
      };
    });
    return (
      <div>
        <Button
          onClick={this.handleAdd}
          type="primary"
          style={{
            marginBottom: 16,
            background: `#509EFC 0% 0% no-repeat padding-box`,
            borderRadius: `6px`,
            opacity: `1`,
            float: `right`,
            marginTop: `107px`,
            marginRight: `10%`,
          }}
        >
          CREATE NEW JOB
        </Button>
        <Table
          components={components}
          rowClassName={() => 'editable-row'}
          dataSource={dataSource}
          columns={columns}
        />
      </div>
    );
  }
}

export default EditableTable;
