import { Table, Input, Button, Modal, Col, Row } from 'antd';
import React, { useState, useEffect } from 'react';

function Modall(props) {
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };
  return (
    <>
      {props.ModalVisible && (

          <Row gutter={[0, 16]}>
            <Col
              xs={{ span: 18, offset: 5 }}
              sm={{ span: 9, offset: 5 }}
              md={{ span: 10, offset: 5 }}
              lg={{ span: 6, offset: 1 }}
            >
              <Input type="text " placeholder="Show result from" />
            </Col>

            <Col
              xs={{ span: 18, offset: 5 }}
              sm={{ span: 9, offset: 5 }}
              md={{ span: 10, offset: 5 }}
              lg={{ span: 6, offset: 1 }}
            >
              <Input type="text " placeholder="Show result to" />
            </Col>

            <Col
              xs={{ span: 18, offset: 5 }}
              sm={{ span: 9, offset: 5 }}
              md={{ span: 10, offset: 5 }}
              lg={{ span: 6, offset: 1 }}
            >
              <Input type="text " />
            </Col>
            <Col
              xs={{ span: 18, offset: 5 }}
              sm={{ span: 9, offset: 5 }}
              md={{ span: 10, offset: 5 }}
              lg={{ span: 6, offset: 1 }}
            >
              <Input type="text " />
            </Col>

            <Col
              xs={{ span: 18, offset: 5 }}
              sm={{ span: 9, offset: 5 }}
              md={{ span: 10, offset: 5 }}
              lg={{ span: 6, offset: 1 }}
            >
              <Input type="text " />
            </Col>

            <Col
              xs={{ span: 18, offset: 5 }}
              sm={{ span: 9, offset: 5 }}
              md={{ span: 10, offset: 5 }}
              lg={{ span: 6, offset: 1 }}
            >
              <Input type="text " />
            </Col>
          </Row>
   
      )}
    </>
  );
}

export default Modal;
