import EditableTable from '../components/table/EditableTable';
import Modall from '../components/Modal/Modall';
import React, { useState, useEffect } from 'react';

function JobApp(props) {
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };
  return (
    <>
         <div
            style={{
              width: `4%`,
              float: `right`,
              background: `#576575`,
              marginRight: `1%`,
              borderRadius: `5px`,
              cursor: `pointer`,
            }}
            onClick={this.showModal}
          >
            <img
              src={filter}
              style={{ width: `80%`, display: `${this.props.showIcon}` }}
              alt="icon-filter"
            />
          </div>
      <EditableTable
        showSelect="none"
        JobLink="Job applications"
        jobTitleT="Job Title"
        jobTitle="title"
        jobLocationT="Job Location"
        jobLocation="location"
        location1="New York"
        location2="London"
        startDateT="Start Date"
        startDate="Sdate"
        endDateT="End Date"
        endDate="Edate"
        statusT="Status"
        status="status"
        showModal={showModal}
      />
      <Modall
        showModal={showModal}
        handleOk={handleOk}
        handleCancel={handleCancel}
      />
    </>
  );
}

export default JobApp;
