import React from 'react';
import './style-form.css';
import { Row, Col, Form, Input, Button, Cascader, Select } from 'antd';

function Forms(props) {
  const { TextArea } = Input;
  const [form] = Form.useForm();

  // const formItemLayout = {
  //   labelCol: {
  //     xs: { span: 24 },
  //     sm: { span: 5 },
  //   },
  //   wrapperCol: {
  //     xs: { span: 24 },
  //     sm: { span: 5 },
  //   },
  // };

  const { Option } = Select;
  const residences = [
    {
      value: 'topic1',
      label: 'topic1',
    },
    {
      value: 'topic2',
      label: 'topic2',
    },
  ];
  return (
    <>
      <Row>
        <Col
          xs={{ span: 8, offset: 0 }}
          sm={{ span: 8, offset: 0 }}
          md={{ span: 10, offset: 0 }}
          lg={{ span: 10, offset: 2 }}
        >
          <Form form={form} layout="vertical">
            <Row style={{ marginTop: '83px' }} className="part-img">
              <Col
                xs={{ span: 8, offset: 0 }}
                sm={{ span: 8, offset: 0 }}
                md={{ span: 10, offset: 0 }}
                lg={{ span: 4, offset: 2 }}
              >
                <Form.Item label="First name">
                  <Input
                    style={{ paddingTop: `12px`, paddingBottom: `12px` }}
                  />
                </Form.Item>
              </Col>
              <Col
                xs={{ span: 8, offset: 0 }}
                sm={{ span: 8, offset: 0 }}
                md={{ span: 10, offset: 0 }}
                lg={{ span: 4, offset: 0 }}
                style={{
                  marginLeft: ` 1%`,
                }}
              >
                <Form.Item label="Last name">
                  <Input
                    style={{
                      paddingTop: `12px`,
                      paddingBottom: `12px`,
                      marginLeft: ` 1%`,
                    }}
                  />
                </Form.Item>
              </Col>
            </Row>

            <Row>
              <Col
                xs={{ span: 8, offset: 0 }}
                sm={{ span: 8, offset: 0 }}
                md={{ span: 10, offset: 0 }}
                lg={{ span: 9, offset: 2 }}
              >
                <Form.Item
                  name="email"
                  label="E-mail"
                  rules={[
                    {
                      type: 'email',
                      message: 'The input is not valid E-mail!',
                    },
                    {
                      // required: true,
                      message: 'Please input your E-mail!',
                    },
                  ]}
                >
                  <Input
                    style={{
                      paddingTop: `12px`,
                      paddingBottom: `12px`,
                      width: `92%`,
                    }}
                  />
                </Form.Item>
              </Col>
            </Row>
            <Row>
              <Col
                xs={{ span: 8, offset: 0 }}
                sm={{ span: 8, offset: 0 }}
                md={{ span: 10, offset: 0 }}
                lg={{ span: 9, offset: 2 }}
              >
                <Form.Item
                  name="Topic"
                  label="Topic"
                  rules={[
                    {
                      type: 'array',
                      // required: true,
                      message: 'Please select the Topic',
                    },
                  ]}
                >
                  <Cascader
                    options={residences}
                    style={{
                      width: `92%`,
                    }}
                  />
                </Form.Item>
              </Col>
            </Row>

            <Row>
              <Col
                xs={{ span: 8, offset: 0 }}
                sm={{ span: 8, offset: 0 }}
                md={{ span: 10, offset: 0 }}
                lg={{ span: 9, offset: 2 }}
              >
                <Form.Item name="Message" label="Message">
                  <TextArea
                    rows={4}
                    style={{
                      width: `92%`,
                    }}
                  />
                </Form.Item>
              </Col>
            </Row>

            <Row>
              <Col
                xs={{ span: 8, offset: 0 }}
                sm={{ span: 8, offset: 0 }}
                md={{ span: 10, offset: 0 }}
                lg={{ span: 9, offset: 2 }}
              >
                <Form.Item>
                  <Button
                    rows={4}
                    style={{
                      width: `92%`,
                      height: `50px`,
                      color: `white`,
                      background: `#94c529`,
                      borderColor: `#94c529`,
                    }}
                  >
                    SUBMIT
                  </Button>
                </Form.Item>
              </Col>
            </Row>
          </Form>
        </Col>

        <Col
          xs={{ span: 8, offset: 0 }}
          sm={{ span: 8, offset: 0 }}
          md={{ span: 10, offset: 0 }}
          lg={{ span: 9, offset: 2 }}
        >

          <p>kfdjgoi</p>
        </Col>
      </Row>
    </>
  );
}

export default Forms;
