import React, { useContext, useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import {
  Table,
  Input,
  Button,
  Popconfirm,
  Form,
  Space,
  Select,
  Modal,
  Col,
  Row,
} from 'antd';
import './style-table.css';
import edit from '../../images/edit.svg';
import deletee from '../../images/delete.svg';
import filter from '../../images/filter.png';
import { Link } from 'react-router-dom';
import Modall from '../Modal/Modall';

const { Option } = Select;
const { Search } = Input;

function EditableTable(props) {
  console.log(props.ModalVisible);
  useEffect(() => {
    setResult(dataSource);
  }, []);

  const {
    jobTitleT,
    jobTitle,
    jobLocation,
    jobLocationT,
    startDate,
    endDate,
    startDateT,
    endDateT,
    statusT,
    statuss,
  } = props;

  const columns = [
    {
      title: '#',
      dataIndex: 'id',
      width: '5%',
      // editable: true,
    },
    {
      title: jobTitleT,
      dataIndex: jobTitle,
    },
    {
      title: jobLocationT,
      dataIndex: jobLocation,
    },
    {
      title: startDateT,
      dataIndex: startDate,
    },
    {
      title: endDateT,
      dataIndex: endDate,
    },
    {
      title: statusT,
      dataIndex: statuss,
    },
    {
      title: '',
      dataIndex: 'operation',
      width: '20%',
      render: (_, record) => (
        <Space size="middle" style={{ cursor: 'pointer' }}>
          {/* <a>Edit</a> */}
          <img src={edit} alt="edit" />
          <Popconfirm
            title="Sure to delete?"
            onConfirm={() => props.handleDelete(record.key)}
          >
            {/* <a>Delete</a> */}
            <img src={deletee} alt="delete" />
          </Popconfirm>
        </Space>
      ),
    },
  ];

  const columnss = columns.map((col) => {
    return {
      ...col,
      onCell: (record) => ({
        record,
        dataIndex: col.dataIndex,
        title: col.title,
      }),
    };
  });
  return (
    <>
      <div className="selectJob">
        <div className="btn-group dropright">
          <span
            className="dropdown-toggle"
            data-toggle="dropdown"
            aria-haspopup="true"
            aria-expanded="false"
            style={{
              font: `normal normal bold 23px/42px Lato`,
              color: `#576575`,
              opacity: `1`,
              cursor: `pointer`,
            }}
          >
            Jobs
          </span>

          <div
            className="dropdown-menu"
            style={{
              top: `9px`,
              borderRadius: '13px',
              background: '#519efc',
            }}
          >
            <Link to="/JobCategories" className="dropdown-item">
              Job categories
            </Link>
            <div class="dropdown-divider"></div>
            <Link to="/JobLocations" className="dropdown-item">
              Job locations
            </Link>
            <div class="dropdown-divider"></div>

            <Link to="/JobApplications" className="dropdown-item">
              Job applications
            </Link>
            <div class="dropdown-divider"></div>
            <a className="dropdown-item">Job on board</a>
            <div class="dropdown-divider"></div>
            <Link to="/Skills" className="dropdown-item">
              Skills
            </Link>
          </div>
          <label style={{ marginTop: '35px', marginLeft: '-53%' }}>
            {props.JobLink}
          </label>
        </div>
      </div>
      <div style={{ paddingTop: `107px`, marginRight: `10%` }}>
        <Link to="/NewJob">
          <Button
            type="primary"
            style={{
              marginBottom: 16,
              background: `#509EFC 0% 0% no-repeat padding-box`,
              borderRadius: `6px`,
              opacity: `1`,
              float: `right`,
            }}
          >
            CREATE NEW
          </Button>
        </Link>
        <Select
          showSearch
          style={{
            width: `15%`,
            float: `right`,
            marginRight: `1%`,
            display: `${props.showSelect}`,
          }}
          placeholder="FILTTER STATUS : ALL "
          optionFilterProp="children"
          onChange={props.onChangeS}
          filterOption={(input, option) =>
            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
          }
        >
          <Option value="all">All</Option>
          <Option value="New York">New York</Option>
          <Option value="London">London</Option>
        </Select>
        <Search
          placeholder="input search text"
          onSearch={props.onSearch}
          style={{
            width: 200,
            float: `right`,
            marginRight: `1%`,
            display: `${props.showSearch}`,
          }}
          className="job-search"
        />
        <div
          style={{
            width: `4%`,
            float: `right`,
            background: `#576575`,
            marginRight: `1%`,
            borderRadius: `5px`,
            cursor: `pointer`,
          }}
          onClick={props.showModal}
        >
          <img
            src={filter}
            style={{ width: `80%`, display: `${props.showIcon}` }}
            alt="icon-filter"
          />
        </div>
      </div>
      {/* ******************************************************************* */}
      <Modall
        visible={props.ModalVisible}
        onOk={props.handleOk}
        onCancel={props.handleCancel}
        onChangeStatus={props.onChangeStatus}
        onChangeLocation={props.onChangeLocation}
      />

      <Table
        rowClassName={() => 'editable-row'}
        dataSource={props.result}
        columns={columnss}
      />
    </>
  );
}

export default EditableTable;
