import EditableTable from '../components/table/EditableTable';
import Options from '../components/options/Options';
import SearchFilter from '../components/search-filter/SearchFilter';
import Buttons from '../components/buttons/Buttons';
import React, { useState, useEffect } from 'react';

function JobApp(props) {
  const columns = [
    {
      title: '#',
      dataIndex: 'id',
      width: '5%',
      // editable: true,
    },

    {
      title: 'Name',
      dataIndex: 'name',
    },

    {
      title: 'Action',
      dataIndex: 'action',
    },

    {
      title: '',
      dataIndex: 'operation',
      width: '20%',
      render: (_, record) => (
        <Space size="large" style={{ cursor: 'pointer', paddingLeft: `63px` }}>
          <img src={edit} alt="edit" />
          <Popconfirm
            title="Sure to delete?"
            onConfirm={() => props.handleDelete(record.key)}
          >
            <img src={deletee} alt="delete" />
          </Popconfirm>
        </Space>
      ),
    },
  ];
  return (
    <>
      <Options JobLink="Job applications" />
      <Buttons />
      <div
        style={{
          paddingTop: `107px`,
          marginRight: `20%`,
          marginBottom: `56px`,
        }}
      >
        <SearchFilter onSearch={props.onSearch} showModal={props.showModal} />
      </div>
      <EditableTable
        jobTitleT="Job Title"
        jobTitle="title"
        jobLocationT="Job Location"
        jobLocation="location"
        startDateT="Start Date"
        startDate="Sdate"
        endDateT="End Date"
        endDate="Edate"
        statusT="Status"
        statuss="status"
        numT="#"
        num="id"
        dataSource={props.dataSource}
        result={props.result}
        setResult={props.setResult}
        handleDelete={props.handleDelete}
        handleOk={props.handleOk}
        handleCancel={props.handleCancel}
        ModalVisible={props.ModalVisible}
        onChangeStatus={props.onChangeStatus}
        onChangeLocation={props.onChangeLocation}
        onChangeStart={props.onChangeStart}
        onChangeEnd={props.onChangeEnd}
      />
    </>
  );
}

export default JobApp;
