import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import React, { useState, useEffect, useContext } from 'react';
import Buttons from './components/buttons/Buttons';
import EditableTable from './components/table/EditableTable';
import Skills from './pages/Skills';
import JobsTable from './pages/JobTable';
import JobLocation from './pages/JobLocation';
import JobCat from './pages/JobCat';
import JobApp from './pages/JobApp';
import NewJob from './pages/NewJob';

function App() {
  const [dataSource, setDataSource] = useState([
    {
      key: '0',
      id: '0',
      title: '32',
      location: 'London',
      Sdate: '5-10',
      Edate: '9-10',
      status: 'Yes',
    },
    {
      key: '1',
      id: ' 1',
      title: '33',
      location: 'New York',
      Sdate: '5-10',
      Edate: '9-10',
      status: 'Yes',
    },
  ]);
  const [count, setCount] = useState(2);
  const [input, setInput] = useState();
  const [ModalVisible, setModalVisible] = useState(false);
  const [status, setStatus] = useState();
  const [location, setLocation] = useState();
  const [result, setResult] = useState([]);

  function handleChanges(newValue, items) {
    setValue(newValue, items);
  }

  useEffect(() => {
    setResult(dataSource);
  }, []);
  const onChangeStatus = (value) => {
    setStatus(value);
  };

  const onChangeLocation = (value) => {
    setLocation(value);
  };

  const onChangeS = (value) => {
    console.log(value);
    const results = !value
      ? dataSource
      : dataSource.filter((item) =>
          item.location.toLowerCase().includes(value.toLowerCase())
        );

    if (value === 'all') {
      setResult(dataSource);
    } else {
      setInput(value);
      setResult(results);
    }
    console.log(results);
    console.log(result);
  };

  const handleDelete = (key) => {
    const dataSource2 = [...result];
    setResult(dataSource2.filter((item) => item.key !== key));
  };

  const onSearch = (value) => {
    console.log(value);
    const result = !value
      ? dataSource
      : dataSource.filter(
          (item) =>
            item.location.toLowerCase().includes(value.toLocaleLowerCase()) ||
            item.title.toLowerCase().includes(value.toLocaleLowerCase()) ||
            item.status.toLowerCase().includes(value.toLocaleLowerCase())
        );
    setResult(result);
  };

  const showModal = () => {
    setModalVisible(true);
  };

  const handleOk = () => {
    const result = ''
      ? dataSource
      : dataSource.filter(
          (item) =>
            item.location
              .toLocaleLowerCase()
              .includes(location.toLocaleLowerCase()) &&
            item.status.toLocaleLowerCase().includes(status.toLocaleLowerCase())
        );
    setModalVisible(false);
    setResult(result);
  };

  const handleCancel = () => {
    setModalVisible(false);
    setStatus('');
    setLocation('');
  };

  const FunContext = React.createContext();

  return (
    <>
      {/* <FunContext.Provider value={handleOk}>
        <EditableTable />
      </FunContext.Provider> */}
      <Router>
        <div className="App">
          <div className="content">
            <Switch>
              <Route exact path="/" component={null} />
              <Route exact path="/JobTable">
                <JobsTable
                  onChangeS={onChangeS}
                  result={result}
                  handleDelete={handleDelete}
                  setResult={setResult}
                />
              </Route>
              <Route exact path="/JobCategories" component={JobCat} />
              <Route exact path="/JobLocations">
                <JobLocation onChangeS={onChangeS} result={result} />
              </Route>
              <Route exact path="/Skills">
                <Skills onChangeS={onChangeS} result={result} />
              </Route>
              <Route exact path="/JobApplications">
                <JobApp onSearch={onSearch} result={result} />
              </Route>
              <Route exact path="/NewJob" component={NewJob} />
            </Switch>
          </div>
        </div>
      </Router>
    </>
  );
}

export default App;
