import PopUpButton from '../components/buttons/PopUpButton';
import MainDropdown from '../components/main-dropdown/MainDropdown';
import EditableTable from '../components/table/EditableTable';
import Options from '../components/options/Options';
import { Popconfirm, Space } from 'antd';
import edit from '../../images/edit.svg';
import deletee from '../../images/delete.svg';

function Skills(props) {
  const columns = [
    {
      title: '#',
      dataIndex: 'id',
      width: '5%',
      // editable: true,
    },
    {
      title: jobTitleT,
      dataIndex: jobTitle,
    },
    {
      title: 'Categories name',
      dataIndex: 'skills',
    },
    {
      title: CategoriesNameT,
      dataIndex: CategoriesName,
    },
    {
      title: 'Skill name',
      dataIndex: jobLocation,
    },
    {
      title: startDateT,
      dataIndex: startDate,
    },
    {
      title: endDateT,
      dataIndex: endDate,
    },
    {
      title: statusT,
      dataIndex: statuss,
    },
    {
      title: '',
      dataIndex: 'operation',
      width: '20%',
      render: (_, record) => (
        <Space size="large" style={{ cursor: 'pointer', paddingLeft: `63px` }}>
          <img src={edit} alt="edit" />
          <Popconfirm
            title="Sure to delete?"
            onConfirm={() => props.handleDelete(record.key)}
          >
            <img src={deletee} alt="delete" />
          </Popconfirm>
        </Space>
      ),
    },
  ];
  return (
    <>
      <Options JobLink="Skills" />
      <PopUpButton
        title="Create Skills"
        selectName="Categories Name"
        Name="Skill Name"
        SkillName={props.SkillName}
        handleChangeSkills={props.handleChangeSkills}
        SaveSelectSkills={props.SaveSelectSkills}
        handleCancelAddJob={props.handleCancelAddJob}
        showModalAddJob={props.showModalAddJob}
        visible2={props.visible2}
        handleOkAddSkills={props.handleOkAddSkills}
      />
      <MainDropdown onChangeS={props.onChangeS} />
      <EditableTable
        skillsT="Categories name"
        skills="skills"
        CategoriesNameT="Skill name"
        CategoriesName="CategoriesName"
        numT="#"
        num="id"
        onChangeS={props.onChangeS}
        result={props.result}
        handleDelete={props.handleDelete}
      />
    </>
  );
}

export default Skills;
