import React, { useState, useEffect } from 'react';
import { Col, Row } from 'antd';
import './style-dragDrop.css';

export default function DragDrop(props) {
  var Newtasks = {
    Applied: [],
    Phone: [],
  };
  var count1 = 0;
  var count2 = 0;
  var countApplied1 = 0;
  var countPhone2 = 0;

  const [tasks, setTasks] = useState([
    {
      name: 'Shahd Bourini',
      special: 'computer Engineer',
      Interviewer: 'AhmAd M, Mosab A',
      AppliedDate: ' 12-12-2021',
      category: 'Applied',
      bgcolor: 'yellow',
    },
    {
      name: 'Shahd',
      special: 'computer Engineer',
      Interviewer: 'AhmAd M, Mosab A',
      AppliedDate: ' 12-12-2021',
      category: 'Applied',
      bgcolor: 'pink',
    },
    {
      name: 'ShBourini',
      special: 'computer Engineer',
      Interviewer: 'AhmAd M, Mosab A',
      AppliedDate: ' 12-12-2021',
      category: 'Phone',
      bgcolor: 'skyblue',
    },
  ]);

  const [taskss, setTaskss] = useState({ Applied: [], Phone: [] });
  const [Applied, setApplied] = useState([]);
  const [Phone, setPhone] = useState([]);
  const [countApplied, setCountApplied] = useState();
  const [countPhone, setCountPhone] = useState();

  useEffect(() => {
    setApplied(Newtasks.Applied[0]);
    setPhone(Newtasks.Phone[1]);

    tasks.map((a) => {
      if (a.category === 'Applied') {
        countApplied1 = countApplied1 + 1;
      } else {
        countPhone2 = countPhone2 + 1;
      }
    });
    setApplied(Newtasks.Applied);
    setCountApplied(countApplied1);
    setCountPhone(countCom);
  }, [tasks]);

  const onDragStart = (ev, id) => {
    // console.log('dragstart:', id);
    ev.dataTransfer.setData('id', id);
  };

  const onDragOver = (ev) => {
    ev.preventDefault();
  };

  const onDrop = (ev, cat) => {
    let id = ev.dataTransfer.getData('id');

    let tasksNew = tasks.filter((task) => {
      if (task.name === id) {
        task.category = cat;
        // console.log(id);
      }
      return task;
    });
    setTasks(tasksNew);
  };

  tasks.forEach((t) => {
    Newtasks[t.category].push(
      <Row
        gutter={[0, 8]}
        style={{ paddingTop: `14px` }}
        key={t.name}
        draggable
        onDragStart={(e) => onDragStart(e, t.name)}
      >
        <Col
          xs={{ span: 24, offset: 0 }}
          sm={{ span: 24, offset: 0 }}
          md={{ span: 24, offset: 0 }}
          lg={{ span: 24, offset: 0 }}
        >
          <div className="DragDrop-cards">
            <div>
              <div className="DragDrop-cards-title">
                <div className="DragDrop-WhiteBox"></div>
                <span>{t.name}</span>
                <br />
                <p>{t.special}</p>
              </div>
              <div className="DragDrop-CardDetails">
                <span>Interviewer : {t.Interviewer}</span>
                <br />
                <span>Applied date: {t.AppliedDate}</span>
              </div>
            </div>
          </div>
        </Col>
      </Row>
    );
  });

  return (
    <>
      <Row gutter={[0, 8]} style={{ paddingTop: `30px` }}>
        <Col
          xs={{ span: 10, offset: 1 }}
          sm={{ span: 10, offset: 1 }}
          md={{ span: 5, offset: 1 }}
          lg={{ span: 5, offset: 1 }}
          onDragOver={(e) => onDragOver(e)}
          onDrop={(e) => onDrop(e, 'Applied')}
        >
          <div className="DragDrop-title">
            <div>
              <h5>Applied</h5>
              <div className="DragDrop-BlueBox">{countApplied}</div>
            </div>
          </div>

          {Newtasks.Applied}
        </Col>

        <Col
          xs={{ span: 10, offset: 1 }}
          sm={{ span: 10, offset: 1 }}
          md={{ span: 5, offset: 0 }}
          lg={{ span: 5, offset: 0 }}
          className="DragDrop-col"
          onDragOver={(e) => onDragOver(e)}
          onDrop={(e) => onDrop(e, 'Phone')}
        >
          <div className="DragDrop-title">
            <div>
              <h5>Phone Screen</h5>
              <div className="DragDrop-BlueBox">{countPhone}</div>
            </div>
          </div>
          {Newtasks.Phone}
        </Col>

        <Col
          xs={{ span: 10, offset: 1 }}
          sm={{ span: 10, offset: 1 }}
          md={{ span: 6, offset: 0 }}
          lg={{ span: 5, offset: 0 }}
          className="DragDrop-col"
        >
          <div className="DragDrop-title">
            <div>
              <h5>Interview</h5>
              <div className="DragDrop-BlueBox">5</div>
            </div>
          </div>
        </Col>

        <Col
          xs={{ span: 10, offset: 1 }}
          sm={{ span: 10, offset: 1 }}
          md={{ span: 5, offset: 0 }}
          lg={{ span: 5, offset: 0 }}
          className="DragDrop-col"
        >
          <div className="DragDrop-title">
            <div>
              <h5>Hired</h5>
              <div className="DragDrop-BlueBox">5</div>
            </div>
          </div>
        </Col>
      </Row>
    </>
  );
}
