import React, { useState, useEffect } from 'react';

export default function DragDrop(props) {
  var Newtasks = {
    wip: [],
    complete: [],
  };
  var count1 = 0;
  var count2 = 0;
  var countWips = 0;
  var countCom = 0;

  const [tasks, setTasks] = useState([
    { name: 'Learn Angular', category: 'wip', bgcolor: 'yellow' },
    { name: 'React', category: 'wip', bgcolor: 'pink' },
    { name: 'Vue', category: 'complete', bgcolor: 'skyblue' },
  ]);

  const [taskss, setTaskss] = useState({ wip: [], complete: [] });
  const [wip, setWip] = useState([]);
  const [complete, setComplete] = useState([]);
  const [countWip, setCountWip] = useState();
  const [countComplete, setCountComplete] = useState();

  useEffect(() => {
    setWip(Newtasks.wip[0]);
    setComplete(Newtasks.complete[1]);

    tasks.map((a) => {
      if (a.category === 'wip') {
        countWips = countWips + 1;
      } else {
        countCom = countCom + 1;
      }
    });
    setCountWip(countWips);
    setCountComplete(countWips);
    console.log(countWips);
  }, [tasks]);

  const onDragStart = (ev, id) => {
    // console.log('dragstart:', id);
    ev.dataTransfer.setData('id', id);
  };

  const onDragOver = (ev) => {
    ev.preventDefault();
  };

  const onDrop = (ev, cat) => {
    let id = ev.dataTransfer.getData('id');

    let tasksNew = tasks.filter((task) => {
      if (task.name === id) {
        task.category = cat;
        // console.log(id);
      }
      return task;
    });
    setTasks(tasksNew);

    // this.setState({
    //   // ...this.state.tasks,
    //   tasks,
    // });
  };

  tasks.forEach((t) => {
    Newtasks[t.category].push(
      <div
        key={t.name}
        onDragStart={(e) => onDragStart(e, t.name)}
        draggable
        className="draggable"
        style={{ backgroundColor: t.bgcolor }}
      >
        {t.name}

        {t.category === 'wip' ? (count1 = count1 + 1) : (count2 = count2 + 1)}
      </div>
    );
    // console.log(Newtasks);
  });

  return (
    <div className="container-drag">
      <h2 className="header">DRAG & DROP DEMO</h2>
      <div
        className="wip"
        onDragOver={(e) => onDragOver(e)}
        onDrop={(e) => {
          onDrop(e, 'wip');
        }}
      >
        <span className="task-header">WIP</span>
        {Newtasks.wip}
      </div>
      <div
        className="droppable"
        onDragOver={(e) => onDragOver(e)}
        onDrop={(e) => onDrop(e, 'complete')}
      >
        <span className="task-header">COMPLETED</span>
        {Newtasks.complete}
      </div>
    </div>
  );
}
