import { Input, DatePicker, Button, Select, Col, Row } from 'antd';
import React, { useState, useEffect } from 'react';
import DragDrop from '../drag-and-drop/DragDrop';
import './style-jobBoard.css';

function JobBoard(props) {
  const { Option } = Select;
  const [job, setJob] = useState();
  const [tasks, setTasks] = useState([
    {
      id: '1',
      name: 'Shahd Bourini',
      special: 'computer Engineer',
      Interviewer: 'AhmAd M, Mosab A',
      AppliedDate: ' 12-12-2021',
      category: 'Applied',
    },
    {
      id: '2',
      name: 'ShBourini',
      special: 'IT',
      Interviewer: 'AhmAd M, Mosab A',
      AppliedDate: ' 12-12-2021',
      category: 'Applied',
    },
    {
      id: '3',
      name: 'Shahd',
      special: 'UI/UX Developer',
      Interviewer: 'AhmAd M, Mosab A',
      AppliedDate: ' 06-06-2020',
      category: 'Interview',
    },
    {
      id: '4',
      name: 'Shahd Bourini',
      special: 'computer Engineer',
      Interviewer: 'AhmAd M, Mosab A',
      AppliedDate: ' 12-12-2021',
      category: 'Phone',
    },
    {
      id: '5',
      name: 'Shahd Al-Bourini',
      special: 'IT',
      Interviewer: 'AhmAd M, Mosab A',
      AppliedDate: ' 12-05-2021',
      category: 'Hired',
    },
  ]);

  const onDragStart = (ev, id) => {
    ev.dataTransfer.setData('id', id);
  };

  const onDragOver = (ev) => {
    ev.preventDefault();
  };

  const onDrop = (ev, cat) => {
    let id = ev.dataTransfer.getData('id');

    let tasksNew = props.tasks.filter((task) => {
      if (task.id === id) {
        task.category = cat;
      }
      return task;
    });
    setTasks(tasksNew);
  };

  const selectResult = (job) => {
    setJob(job);
  };

  const applyFilter = () => {
    console.log(job);
  };
  return (
    <>
      <Row gutter={[0, 8]} style={{ paddingTop: `30px` }}>
        <Col
          xs={{ span: 7, offset: 1 }}
          sm={{ span: 7, offset: 1 }}
          md={{ span: 4, offset: 1 }}
          lg={{ span: 4, offset: 1 }}
        >
          <DatePicker
            placeholder="Show result from"
            className="JobBoard-input"
          />
        </Col>

        <Col
          xs={{ span: 7, offset: 0 }}
          sm={{ span: 7, offset: 0 }}
          md={{ span: 4, offset: 0 }}
          lg={{ span: 4, offset: 0 }}
        >
          <DatePicker placeholder="Show result to" className="JobBoard-input" />
        </Col>

        <Col
          xs={{ span: 7, offset: 0 }}
          sm={{ span: 7, offset: 0 }}
          md={{ span: 4, offset: 0 }}
          lg={{ span: 4, offset: 0 }}
        >
          <Select
            style={{ width: `95%`, borderRadius: `6px` }}
            className="JobBoard-input"
            placeholder="All Jobs"
            onChange={selectResult}
          >
            <Option value="UI/UX Developer">UI/UX Developer</Option>
            <Option value="Computer Engineer">Computer Engineer</Option>
            <Option value="IT">IT</Option>
          </Select>
        </Col>

        <Col
          xs={{ span: 20, offset: 1 }}
          sm={{ span: 11, offset: 1 }}
          md={{ span: 9, offset: 0 }}
          lg={{ span: 10, offset: 0 }}
        >
          <div className="jobBoard">
            <Button onClick={applyFilter}>Apply</Button>
            <Button>Reset</Button>
            <Button>Table View</Button>
          </div>
        </Col>
      </Row>
      <DragDrop tasks={tasks} onDragStart={onDragStart} onDragOver={onDragOver} />
    </>
  );
}

export default JobBoard;
