import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import Skills from './pages/Skills';
import JobsTable from './pages/JobTable';
import JobLocation from './pages/JobLocation';
import JobCat from './pages/JobCat';
import JobApp from './pages/JobApp';
import NewJob from './pages/NewJob';
import JobOpening from './components/job-opening/JobOpening';

function App() {
  const [dataSource, setDataSource] = useState([
    {
      key: '0',
      id: '0',
      title: '32',
      location: 'London',
      skills: 'skill1',
      CategoriesName: 'test1',
      CatName: 'test1',
      Sdate: '2021-02-23',
      Edate: '2023-02-23',
      status: 'Yes',
    },
    {
      key: '1',
      id: '1',
      title: '33',
      skills: 'skill2',
      CatName: 'test1',
      CategoriesName: 'test2',
      location: 'New York',
      Sdate: '2020-03-23',
      Edate: '2022-02-23',
      status: 'Yes',
    },
    {
      key: '2',
      id: '2',
      title: '34',
      skills: 'skill3',
      CatName: 'test1',
      CategoriesName: 'test3',
      location: 'Nablus',
      Sdate: '2019-02-25',
      Edate: '2019-10-23',
      status: 'No',
    },
    {
      key: '3',
      id: '3',
      title: '35',
      skills: 'skill4',
      CatName: 'test1',
      CategoriesName: 'test4',
      location: 'London',
      Sdate: '2021-02-23',
      Edate: '2023-02-23',
      status: 'Yes',
    },
    {
      key: '4',
      id: '4',
      title: '36',
      skills: 'skill5',
      CatName: 'test1',
      CategoriesName: 'test5',
      location: 'Palestine',
      Sdate: '2020-06-23',
      Edate: '2020-12-23',
      status: 'No',
    },
  ]);
  const [input, setInput] = useState();
  const [ModalVisible, setModalVisible] = useState(false);
  const [status, setStatus] = useState();
  const [startDate, setStartDate] = useState();
  const [EndDate, setEndDate] = useState();
  const [result, setResult] = useState([]);

  function onChangeStart(date, dateString) {
    console.log(date, dateString);
    setStartDate(dateString);
  }

  function onChangeEnd(date, dateString) {
    console.log(date, dateString);
    setEndDate(dateString);
  }

  function handleChanges(newValue) {
    setResult(newValue);
  }

  useEffect(() => {
    setResult(dataSource);
  }, []);
  const onChangeStatus = (value) => {
    setStatus(value);
  };

  const onChangeS = (value) => {
    console.log(value);
    const results = !value
      ? dataSource
      : dataSource.filter(
          (item) =>
            item.location.toLowerCase().includes(value.toLowerCase()) ||
            item.skills.toLowerCase().includes(value.toLowerCase()) ||
            item.CatName.toLowerCase().includes(value.toLowerCase())
        );

    if (value === 'all') {
      setResult(dataSource);
    } else {
      setInput(value);
      setResult(results);
    }
    console.log(results);
    console.log(result);
  };

  const handleDelete = (id) => {
    const dataSource2 = [...result];
    setResult(dataSource2.filter((item) => item.id !== id));
  };

  const onSearch = (value) => {
    console.log(value);
    const result = !value
      ? dataSource
      : dataSource.filter(
          (item) =>
            item.location.toLowerCase().includes(value.toLocaleLowerCase()) ||
            item.title.toLowerCase().includes(value.toLocaleLowerCase()) ||
            item.status.toLowerCase().includes(value.toLocaleLowerCase())
        );
    setResult(result);
  };

  const showModal = () => {
    setModalVisible(true);
  };
  /*2020-03-23  2022-02-23*/
  const handleOk = () => {
    const result = ''
      ? dataSource
      : dataSource.filter(
          (item) =>
            // item.location
            //   .toLocaleLowerCase()
            //   .includes(location.toLocaleLowerCase()) &&
            item.status
              .toLocaleLowerCase()
              .includes(status.toLocaleLowerCase()) &&
            item.Sdate > startDate &&
            item.Edate <= EndDate
        );
    setModalVisible(false);
    setResult(result);
  };

  const handleCancel = () => {
    setModalVisible(false);
  };

  return (
    <>
      <Router>
        <div className="App">
          <div className="content">
            <Switch>
              <Route exact path="/" component={null} />
              <Route exact path="/JobTable">
                <JobsTable
                  onChangeS={onChangeS}
                  result={result}
                  handleDelete={handleDelete}
                  setResult={setResult}
                  onChange={handleChanges}
                />
              </Route>
              <Route exact path="/JobCategories">
                <JobCat />
              </Route>
              <Route exact path="/JobLocations">
                <JobLocation />
              </Route>
              <Route exact path="/Skills">
                <Skills />
              </Route>
              <Route exact path="/JobApplications">
                <JobApp
                  onSearch={onSearch}
                  result={result}
                  handleDelete={handleDelete}
                  showModal={showModal}
                  handleOk={handleOk}
                  handleCancel={handleCancel}
                  ModalVisible={ModalVisible}
                  onChangeStatus={onChangeStatus}
                  // onChangeLocation={onChangeLocation}
                  onChangeStart={onChangeStart}
                  onChangeEnd={onChangeEnd}
                />
              </Route>
              <Route exact path="/NewJob" component={NewJob} />
              <Route
                exact
                path="/jobOpenings"
                component={JobOpening}

              />
            </Switch>
          </div>
        </div>
      </Router>
    </>
  );
}

export default App;
