import EditableTable from '../components/table/EditableTable';
import PopUpButton from '../components/buttons/PopUpButton';
import MainDropdown from '../components/main-dropdown/MainDropdown';
import Options from '../components/options/Options';
import { Popconfirm, Space } from 'antd';
import edit from '../images/edit.svg';
import deletee from '../images/delete.svg';
import React, { useState, useEffect } from 'react';

function JobLocation(props) {
  const [countLocation, setCountLocation] = useState(0);
  const [location, setLocation] = useState();
  const [jobLocation, setJobLocation] = useState();
  const [jobName, setJobName] = useState('');
  const [resultLocationTable, setResultLocationTable] = useState([]);
  const [resultJobs, setResultJobs] = useState([]);
  const [visible2, setVisible2] = useState(false);
  
  const columns = [
    {
      title: '#',
      dataIndex: 'id',
      width: '5%',
      // editable: true,
    },

    {
      title: 'Name',
      dataIndex: 'title',
    },

    {
      title: 'Location',
      dataIndex: 'location',
    },

    {
      title: '',
      dataIndex: 'operation',
      width: '20%',
      render: (_, record) => (
        <Space size="large" style={{ cursor: 'pointer', paddingLeft: `63px` }}>
          <img src={edit} alt="edit" />
          <Popconfirm
            title="Sure to delete?"
            onConfirm={() => props.handleDeleteLocations(record.key)}
          >
            <img src={deletee} alt="delete" />
          </Popconfirm>
        </Space>
      ),
    },
  ];
  return (
    <>
      <Options JobLink="Job Locations" />
      <PopUpButton
        title="Locations"
        Name="Job location name"
        selectName="Country"
        SaveSelectLocation={props.SaveSelectLocation}
        jobLocation={props.JobName}
        handleChangeLocation={props.handleChangeLocation}
        handleOkAddJob={props.handleOkAddJob}
        handleCancelAddJob={props.handleCancelAddJob}
        showModalAddJob={props.showModalAddJob}
        visible2={props.visible2}
      />
      <MainDropdown onChangeS={props.onChangeS} />
      <EditableTable
        result={props.result}
        handleDelete={props.handleDelete}
        columns={columns}
      />
    </>
  );
}

export default JobLocation;
