import { Input, DatePicker, Button, Select, Col, Row } from 'antd';
import DragDrop from '../drag-and-drop/DragDrop';
import './style-jobBoard.css';

function JobBoard(props) {
  const { Option } = Select;

  const selectResult = (job) => {
    console.log(job);
  };

  return (
    <>
      <Row gutter={[0, 8]} style={{ paddingTop: `30px` }}>
        <Col
          xs={{ span: 20, offset: 1 }}
          sm={{ span: 4, offset: 1 }}
          md={{ span: 4, offset: 1 }}
          lg={{ span: 4, offset: 1 }}
        >
          <DatePicker
            placeholder="Show result from"
            className="JobBoard-input"
          />
        </Col>

        <Col
          xs={{ span: 20, offset: 1 }}
          sm={{ span: 22, offset: 2 }}
          md={{ span: 4, offset: 0 }}
          lg={{ span: 4, offset: 0 }}
        >
          <DatePicker placeholder="Show result to" className="JobBoard-input" />
        </Col>

        <Col
          xs={{ span: 20, offset: 1 }}
          sm={{ span: 22, offset: 2 }}
          md={{ span: 4, offset: 0 }}
          lg={{ span: 4, offset: 0 }}
        >
          <Select
            style={{ width: 200, borderRadius: `6px` }}
            className="JobBoard-input"
            placeholder="All Jobs"
            onChange={selectResult}
          >
            <Option value="ui/ux">UI/UX Developer</Option>
            <Option value="computerEngineer">Computer Engineer</Option>
            <Option value="it">IT</Option>
          </Select>
        </Col>

        <Col
          xs={{ span: 20, offset: 1 }}
          sm={{ span: 22, offset: 2 }}
          md={{ span: 8, offset: 2 }}
          lg={{ span: 10, offset: 1 }}
        >
          <div className="jobBoard">
            <Button>Apply</Button>
            <Button>Reset</Button>
            <Button>Table View</Button>
          </div>
        </Col>
      </Row>
      <DragDrop />
    </>
  );
}

export default JobBoard;
