import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import Slider from 'react-slick';
import ReactDOM from 'react-dom';
import { Row, Col } from 'antd';
import 'antd/dist/antd.css';
import './style-slide.css';
import img1 from '../../images/img1.svg';
import img2 from '../../images/img2.svg';
import img3 from '../../images/img3.svg';
import img4 from '../../images/img4.svg';
import img5 from '../../images/img5.svg';

function Slide(props) {
  var settings = {
    dots: true,
    arrows: true,
  };
  return (
    <div>
      <div className="container">
        <Slider {...settings}>
          <div>
            <Row>
              <Col
                xs={{ span: 3, offset: 1 }}
                sm={{ span: 3, offset: 1 }}
                md={{ span: 6, offset: 1 }}
                lg={{ span: 4, offset: 1 }}
              >
                <div className="line"></div>
                <div className="slide-details1">
                  <p>
                    Select theme, identify clusters, research segments, map
                    options and develop PoV/Pitch.
                  </p>
                </div>
              </Col>

              <Col
                xs={{ span: 30, offset: 0 }}
                sm={{ span: 15, offset: 0 }}
                md={{ span: 13, offset: 0 }}
                lg={{ span: 14, offset: 0 }}
              >
                <div className="container">
                  <div className="rounded-img">
                    <img src={img5} />
                  </div>
                </div>
              </Col>

              <Col
                xs={{ span: 30, offset: 0 }}
                sm={{ span: 4, offset: 0 }}
                md={{ span: 4, offset: 0 }}
                lg={{ span: 7, offset: 0 }}
              >
                <div className="slide-details">
                  <h1>Select</h1>
                  <p>
                    Select theme, identify clusters, research segments, map
                    options and develop PoV/Pitch.
                  </p>
                </div>
              </Col>
            </Row>
          </div>
          <div>
            <Row>
              <Col
                xs={{ span: 30, offset: 0 }}
                sm={{ span: 20, offset: 1 }}
                md={{ span: 17, offset: 4 }}
                lg={{ span: 12, offset: 5 }}
              >
                <div className="container">
                  <div className="rounded-img">
                    <img src={img2} />
                  </div>
                </div>
              </Col>
            </Row>
          </div>
          <div>
            <Row>
              <Col
                xs={{ span: 30, offset: 0 }}
                sm={{ span: 20, offset: 1 }}
                md={{ span: 17, offset: 4 }}
                lg={{ span: 12, offset: 5 }}
              >
                <div className="container">
                  <div className="rounded-img">
                    <img src={img3} />
                  </div>
                </div>
              </Col>
            </Row>
          </div>
          <div>
            <Row>
              <Col
                xs={{ span: 30, offset: 0 }}
                sm={{ span: 20, offset: 1 }}
                md={{ span: 17, offset: 4 }}
                lg={{ span: 12, offset: 5 }}
              >
                <div className="container">
                  <div className="rounded-img">
                    <img src={img4} />
                  </div>
                </div>
              </Col>
            </Row>
          </div>
          <div>
            <Row>
              <Col
                xs={{ span: 30, offset: 0 }}
                sm={{ span: 20, offset: 1 }}
                md={{ span: 17, offset: 4 }}
                lg={{ span: 12, offset: 5 }}
              >
                <div className="container">
                  <div className="rounded-img">
                    <img src={img1} />
                  </div>
                </div>
              </Col>
            </Row>
          </div>
        </Slider>
      </div>
    </div>
  );
}

export default Slide;
