import { Form, Input, Button } from 'antd';
import './style-jobApply.css';

function ApplyForm(props) {
  const layout = {
    labelCol: {
      lg: { span: 2, offset: 3 },
      md: { span: 3, offset: 3 },
      sm: { span: 3, offset: 3 },
      xs: { span: 4, offset: 1 },
    },
    wrapperCol: {
      lg: { span: 16, offset: 5 },
      md: { span: 14, offset: 5 },
      sm: { span: 13, offset: 3 },
      xs: { span: 10, offset: 3 },
    },
  };
  /* eslint-disable no-template-curly-in-string */

  const validateMessages = {
    required: '${label} is required!',
    types: {
      email: '${label} is not a valid email!',
      number: '${label} is not a valid number!',
    },
    number: {
      range: '${label} must be between ${min} and ${max}',
    },
  };
  const onFinish = (values) => {
    console.log(values);
  };
  return (
    <>
      <Form
        {...layout}
        name="nest-messages"
        onFinish={onFinish}
        validateMessages={validateMessages}
        style={{
          paddingTop: `57px`,
        }}
      >
        <Form.Item
          name={['user', 'name']}
          label="Full Name:"

        >
          <Input className="ApplyForm-input" />
        </Form.Item>
        <Form.Item
          name={['user', 'age']}
          label="Date of birth:"
     
        >
          <Input className="ApplyForm-input" />
        </Form.Item>
        <Form.Item
          name={['user', 'website']}
          label="Location:"
  
        >
          <Input className="ApplyForm-input" />
        </Form.Item>
        <Form.Item
          name={['user', 'website']}
          label="Upload CV:"
          labelCol={{ ...layout.labelCol, offset: 3 }}
        >
          <Input className="ApplyForm-input" />
        </Form.Item>
        <Form.Item
          name={['user', 'introduction']}
          label="More info:"
          labelCol={{ ...layout.labelCol, offset: 5 }}
        >
          <Input.TextArea rows={4} className="ApplyForm-input" />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit" className="ApplyForm-submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    </>
  );
}

export default ApplyForm;
