import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import React, { useState, useEffect, useContext } from 'react';
import Buttons from './components/buttons/Buttons';
import EditableTable from './components/table/EditableTable';
import Skills from './pages/Skills';
import JobsTable from './pages/JobTable';
import JobLocation from './pages/JobLocation';
import JobCat from './pages/JobCat';
import JobApp from './pages/JobApp';
import NewJob from './pages/NewJob';

function App() {
  const onChangeStatus = (value) => {
    this.setState({
      status: value,
    });
  };

  const onChangeLocation = (value) => {
    this.setState({
      location: value,
    });
  };

  const onChangeS = (value) => {


    const dataSource = [...this.state.dataSource];
    const result = !value
      ? dataSource
      : dataSource.filter((item) => item.location.includes(value));

    if (value === 'all') {
      this.setState({
        result: dataSource,
      });
    } else {
      this.setState({
        input: value,
        result: result,
      });
    }
    console.log(result);
  };

  const handleDelete = (key) => {
    const dataSource2 = [...this.state.result];
    this.setState({
      result: dataSource2.filter((item) => item.key !== key),
    });
  };

  const onSearch = (value) => {
    // console.log(value);
    const dataSource = [...this.state.dataSource];
    const result = !value
      ? dataSource
      : dataSource.filter(
          (item) =>
            item.location.toLowerCase().includes(value.toLocaleLowerCase()) ||
            item.title.toLowerCase().includes(value.toLocaleLowerCase()) ||
            item.status.toLowerCase().includes(value.toLocaleLowerCase())
        );
    this.setState({
      result: result,
    });
  };

  const showModal = () => {
    this.setState({
      ModalVisible: true,
    });
  };

  const handleOk = () => {
    const dataSource = [...this.state.dataSource];
    const result = ''
      ? dataSource
      : dataSource.filter(
          (item) =>
            item.location
              .toLocaleLowerCase()
              .includes(this.state.location.toLocaleLowerCase()) &&
            item.status
              .toLocaleLowerCase()
              .includes(this.state.status.toLocaleLowerCase())
        );
    this.setState({
      ModalVisible: false,
      result: result,
    });
  };

  const handleCancel = () => {
    this.setState({
      ModalVisible: false,
      status: '',
      location: '',
    });
  };

  const FunContext = React.createContext();

  return (
    <>
      <FunContext.Provider value={handleOk}>
        <EditableTable />
      </FunContext.Provider>
      <Router>
        <div className="App">
          <div className="content">
            <Switch>
              <Route exact path="/" component={null} />
              <Route exact path="/JobTable" component={JobsTable} />
              <Route exact path="/JobCategories" component={JobCat} />
              <Route exact path="/JobLocations" component={JobLocation} />
              <Route exact path="/Skills" component={Skills} />
              <Route exact path="/JobApplications" component={JobApp} />

              <Route exact path="/NewJob" component={NewJob} />
            </Switch>
          </div>
        </div>
      </Router>
    </>
  );
}

export default App;
