import React, { useContext, useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import { Table, Input, Button, Popconfirm, Form, Space, Select } from 'antd';
import './style-table.css';
import edit from '../../images/edit.svg';
import deletee from '../../images/delete.svg';
import { Link } from 'react-router-dom';

const EditableContext = React.createContext(null);

const EditableRow = ({ index, ...props }) => {
  const [form] = Form.useForm();
  return (
    <Form form={form} component={false}>
      <EditableContext.Provider value={form}>
        <tr {...props} />
      </EditableContext.Provider>
    </Form>
  );
};

const { Option } = Select;

const EditableCell = ({
  title,
  editable,
  children,
  dataIndex,
  record,
  handleSave,
  ...restProps
}) => {
  const [editing, setEditing] = useState(false);
  const inputRef = useRef(null);
  const form = useContext(EditableContext);
  useEffect(() => {
    if (editing) {
      inputRef.current.focus();
    }
  }, [editing]);

  const toggleEdit = () => {
    setEditing(!editing);
    form.setFieldsValue({
      [dataIndex]: record[dataIndex],
    });
  };

  const save = async () => {
    try {
      const values = await form.validateFields();
      toggleEdit();
      handleSave({ ...record, ...values });
    } catch (errInfo) {
      console.log('Save failed:', errInfo);
    }
  };

  let childNode = children;

  if (editable) {
    childNode = editing ? (
      <Form.Item
        style={{
          margin: 0,
        }}
        name={dataIndex}
        rules={[
          {
            required: true,
            message: `${title} is required.`,
          },
        ]}
      >
        <Input ref={inputRef} onPressEnter={save} onBlur={save} />
      </Form.Item>
    ) : (
      <div
        className="editable-cell-value-wrap"
        style={{
          paddingRight: 24,
        }}
        onClick={toggleEdit}
      >
        {children}
      </div>
    );
  }

  return <td {...restProps}>{childNode}</td>;
};

class EditableTable extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      dataSource: [
        {
          key: '0',
          id: '0',
          title: '32',
          location: 'London',
          Sdate: '5-10',
          Edate: '9-10',
          status: 'yes',
        },
        {
          key: '1',
          id: ' 1',
          title: '32',
          location: 'New York',
          Sdate: '5-10',
          Edate: '9-10',
          status: 'yes',
        },
      ],
      count: 2,
      input: '',
      result: '',
    };
  }

  componentDidMount() {
    this.setState({
      result: this.state.dataSource,
    });
  }

  onChangeS = (value) => {
    // console.log(`selected ${value}`);

    const dataSource = [...this.state.dataSource];
    const result = !value
      ? dataSource
      : dataSource.filter((item) => item.location.includes(value));

    if (value === 'all') {
      this.setState({
        result: dataSource,
      });
    } else {
      this.setState({
        input: value,
        result: result,
      });
    }
    console.log(result);
  };

  handleDelete = (key) => {
    const dataSource2 = [...this.state.result];
    this.setState({
      result: dataSource2.filter((item) => item.key !== key),
    });
  };
  handleAdd = () => {
    const { count, dataSource, result } = this.state;
    const newData = {
      key: count,
      id: `${count}`,
      title: '32',
      location: `London, Park Lane no. ${count}`,
      Sdate: '5-10',
      Edate: '9-10',
      status: 'yes',
    };
    this.setState({
      dataSource: [...dataSource, newData],
      result: [...result, newData],
      count: count + 1,
    });
  };
  handleSave = (row) => {
    // const newData = [...this.state.dataSource];
    const newData2 = [...this.state.result];
    // const index = newData.findIndex((item) => row.key === item.key);
    const index2 = newData2.findIndex((item) => row.key === item.key);
    // const item = newData[index];
    const item2 = newData2[index2];
    // newData.splice(index, 1, { ...item, ...row });
    newData2.splice(index2, 1, { ...item2, ...row });
    this.setState({
      // dataSource: newData,
      result: newData2,
    });
  };

  render() {
    const { dataSource, result } = this.state;
    const { jobTitle, jobLocation, startDate, endDate } = this.props;

    this.columns = [
      {
        title: '#',
        dataIndex: 'id',
        width: '5%',
        // editable: true,
      },
      {
        title: jobTitle,
        dataIndex: 'title',
      },
      {
        title: jobjobLocation,
        dataIndex: 'jobLocation',
      },
      {
        title: startDate,
        dataIndex: 'Sdate',
      },
      {
        title: endDate,
        dataIndex: 'Edate',
      },
      {
        title: 'Status',
        dataIndex: 'status',
      },
      {
        title: '',
        dataIndex: 'operation',
        width: '20%',
        render: (_, record) => (
          <Space size="middle" style={{ cursor: 'pointer' }}>
            {/* <a>Edit</a> */}
            <img src={edit} alt="edit" />
            <Popconfirm
              title="Sure to delete?"
              onConfirm={() => this.handleDelete(record.key)}
            >
              {/* <a>Delete</a> */}
              <img src={deletee} alt="delete" />
            </Popconfirm>
          </Space>
        ),
      },
    ];

    const components = {
      body: {
        row: EditableRow,
        cell: EditableCell,
      },
    };
    const columns = this.columns.map((col) => {
      if (!col.editable) {
        return col;
      }

      return {
        ...col,
        onCell: (record) => ({
          record,
          editable: col.editable,
          dataIndex: col.dataIndex,
          title: col.title,
          handleSave: this.handleSave,
        }),
      };
    });
    return (
      <div>
        <div className="selectJob">
          <div className="btn-group dropright">
            <span
              className="dropdown-toggle"
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
              style={{
                font: `normal normal bold 23px/42px Lato`,
                color: `#576575`,
                opacity: `1`,
                cursor: `pointer`,
              }}
            >
              Jobs
            </span>
            <div
              className="dropdown-menu"
              style={{
                top: `9px`,
                borderRadius: '13px',
                background: '#519efc',
              }}
            >
              <a className="dropdown-item">Job categories</a>
              <div class="dropdown-divider"></div>
              <a className="dropdown-item">Job locations</a>
              <div class="dropdown-divider"></div>
              <a className="dropdown-item">Job applications</a>
              <div class="dropdown-divider"></div>
              <a className="dropdown-item">Job on board</a>
              <div class="dropdown-divider"></div>
              <a className="dropdown-item">Skills</a>
            </div>
          </div>
        </div>

        <Link to="/NewJob">
          <Button
            onClick={this.handleAdd}
            type="primary"
            style={{
              marginBottom: 16,
              background: `#509EFC 0% 0% no-repeat padding-box`,
              borderRadius: `6px`,
              opacity: `1`,
              float: `right`,
              marginTop: `107px`,
              marginRight: `10%`,
            }}
          >
            CREATE NEW JOB
          </Button>
        </Link>

        <Select
          showSearch
          style={{
            width: `15%`,
            float: `right`,
            marginRight: `1%`,
            marginTop: `106px`,
          }}
          placeholder="FILTTER STATUS : ALL "
          optionFilterProp="children"
          onChange={this.onChangeS}
          filterOption={(input, option) =>
            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
          }
        >
          <Option value="all">All</Option>
          <Option value="New York">New York</Option>
          <Option value="London">London</Option>
        </Select>

        <Table
          components={components}
          rowClassName={() => 'editable-row'}
          dataSource={result}
          columns={columns}
          //   onChange={onChange}
        />
      </div>
    );
  }
}

export default EditableTable;
