import React, { useState, useEffect } from 'react';
import { Col, Row } from 'antd';
import './style-dragDrop.css';

export default function DragDrop(props) {
  var Newtasks = {
    wip: [],
    complete: [],
  };
  var count1 = 0;
  var count2 = 0;
  var countWips = 0;
  var countCom = 0;

  const [tasks, setTasks] = useState([
    { name: 'Learn Angular', category: 'wip', bgcolor: 'yellow' },
    { name: 'React', category: 'wip', bgcolor: 'pink' },
    { name: 'Vue', category: 'complete', bgcolor: 'skyblue' },
  ]);

  const [taskss, setTaskss] = useState({ wip: [], complete: [] });
  const [wip, setWip] = useState([]);
  const [complete, setComplete] = useState([]);
  const [countWip, setCountWip] = useState();
  const [countComplete, setCountComplete] = useState();

  useEffect(() => {
    setWip(Newtasks.wip[0]);
    setComplete(Newtasks.complete[1]);

    tasks.map((a) => {
      if (a.category === 'wip') {
        countWips = countWips + 1;
      } else {
        countCom = countCom + 1;
      }
    });
    setWip(Newtasks.wip);
    setCountWip(countWips);
    setCountComplete(countCom);
  }, [tasks]);

  const onDragStart = (ev, id) => {
    // console.log('dragstart:', id);
    ev.dataTransfer.setData('id', id);
  };

  const onDragOver = (ev) => {
    ev.preventDefault();
  };

  const onDrop = (ev, cat) => {
    let id = ev.dataTransfer.getData('id');

    let tasksNew = tasks.filter((task) => {
      if (task.name === id) {
        task.category = cat;
        // console.log(id);
      }
      return task;
    });
    setTasks(tasksNew);

    // this.setState({
    //   // ...this.state.tasks,
    //   tasks,
    // });
  };

  tasks.forEach((t) => {
    Newtasks[t.category].push(
      <div
        key={t.name}
        onDragStart={(e) => onDragStart(e, t.name)}
        draggable
        className="DragDrop-cards"
      >
        {t.name}

        {t.category === 'wip' ? (count1 = count1 + 1) : (count2 = count2 + 1)}
      </div>
    );
  });

  return (
    <>
      <Row gutter={[0, 8]} style={{ paddingTop: `30px` }}>
        <Col
          xs={{ span: 10, offset: 1 }}
          sm={{ span: 10, offset: 1 }}
          md={{ span: 5, offset: 1 }}
          lg={{ span: 5, offset: 1 }}
        >
          <div className="DragDrop-title">
            <div>
              <h5>Applied</h5>
              <div className="DragDrop-BlueBox">5</div>
            </div>
          </div>

          <Row gutter={[0, 8]} style={{ paddingTop: `30px` }}>
            <Col
              xs={{ span: 24, offset: 0 }}
              sm={{ span: 24, offset: 0 }}
              md={{ span: 24, offset: 0 }}
              lg={{ span: 24, offset: 0 }}
            >
               <div className="DragDrop-cards">
                <div>
                  <div className="DragDrop-cards-title">
                    <div className="DragDrop-WhiteBox"></div>
                    <span>Shahd Bourini</span>
                    <br />
                    <p>Computer Engineer</p>
                  </div>
                  <div className="DragDrop-CardDetails">
                    <span>Interviewer : AhmAd M, Mosab A</span>
                    <br />
                    <span>Applied date: 12-12-2021</span>
                  </div>
                </div>
              </div>
            </Col>
          </Row>
        </Col>

        <Col
          xs={{ span: 10, offset: 1 }}
          sm={{ span: 10, offset: 1 }}
          md={{ span: 5, offset: 0 }}
          lg={{ span: 5, offset: 0 }}
          className="DragDrop-col"
        >
          <div className="DragDrop-title">
            <div>
              <h5>Phone Screen</h5>
              <div className="DragDrop-BlueBox">5</div>
            </div>
          </div>
          <Row gutter={[0, 8]} style={{ paddingTop: `30px` }}>
            <Col
              xs={{ span: 24, offset: 0 }}
              sm={{ span: 24, offset: 0 }}
              md={{ span: 24, offset: 0 }}
              lg={{ span: 24, offset: 0 }}
            >
              <div
                className="DragDrop-cards"
                onDragOver={(e) => onDragOver(e)}
                onDrop={(e) => onDrop(e, 'complete')}
              >
                <div>
                  <div className="DragDrop-cards-title">
                    {/* <div className="DragDrop-WhiteBox"></div>
                    <span>Shahd Bourini</span>
                    <br />
                    <p>Computer Engineer</p> */}
                    {Newtasks.complete}
                  </div>
                  {/* <div className="DragDrop-CardDetails">
                    <span>Interviewer : AhmAd M, Mosab A</span>
                    <br />
                    <span>Applied date: 12-12-2021</span>
                  </div> */}
                </div>
              </div>
            </Col>
          </Row>
        </Col>

        <Col
          xs={{ span: 10, offset: 1 }}
          sm={{ span: 10, offset: 1 }}
          md={{ span: 6, offset: 0 }}
          lg={{ span: 5, offset: 0 }}
          className="DragDrop-col"
        >
          <div className="DragDrop-title">
            <div>
              <h5>Interview</h5>
              <div className="DragDrop-BlueBox">5</div>
            </div>
          </div>
        </Col>

        <Col
          xs={{ span: 10, offset: 1 }}
          sm={{ span: 10, offset: 1 }}
          md={{ span: 5, offset: 0 }}
          lg={{ span: 5, offset: 0 }}
          className="DragDrop-col"
        >
          <div className="DragDrop-title">
            <div>
              <h5>Hired</h5>
              <div className="DragDrop-BlueBox">5</div>
            </div>
          </div>
        </Col>
      </Row>
    </>
  );
}
