import { Form, Input, Button } from 'antd';
import './style-jobApply.css';

function ApplyForm(props) {
  const layout = {
    labelCol: {
      span: 2,
    },
    wrapperCol: {
        span: 20,
    },
  };
  /* eslint-disable no-template-curly-in-string */

  const validateMessages = {
    required: '${label} is required!',
    types: {
      email: '${label} is not a valid email!',
      number: '${label} is not a valid number!',
    },
    number: {
      range: '${label} must be between ${min} and ${max}',
    },
  };
  const onFinish = (values) => {
    console.log(values);
  };
  return (
    <>
      <Form
        {...layout}
        name="nest-messages"
        onFinish={onFinish}
        validateMessages={validateMessages}
        style={{
          width: `100%`,
          paddingRight: `17%`,
          paddingLeft: `10%`,
          paddingTop: `57px`,
        }}
      >
        <Form.Item name={['user', 'name']} label="Full Name:">
          <Input />
        </Form.Item>
        <Form.Item name={['user', 'age']} label="Date of birth:">
          <Input />
        </Form.Item>
        <Form.Item name={['user', 'website']} label="Location:">
          <Input />
        </Form.Item>
        <Form.Item name={['user', 'website']} label="Upload CV:">
          <Input />
        </Form.Item>
        <Form.Item name={['user', 'introduction']} label="More info:">
          <Input.TextArea />
        </Form.Item>
        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 5 }}>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    </>
  );
}

export default ApplyForm;
