import EditableTable from '../components/table/EditableTable';
import Buttons from '../components/buttons/Buttons';
import MainDropdown from '../components/main-dropdown/MainDropdown';
import Options from '../components/options/Options';
import PopUpButton from '../components/buttons/PopUpButton';
import { Popconfirm, Space } from 'antd';
import edit from '../images/edit.svg';
import deletee from '../images/delete.svg';
import React, { useState, useEffect } from 'react';

function JobCat(props) {
  const [countCat, setCountCat] = useState(0);
  const [resultCatTable, setResultCatTable] = useState([]);
  const [CatName, setCatName] = useState('');
  const [visible2, setVisible2] = useState(false);

  const handleDeleteCat = (id) => {
    const dataSource2 = [...resultCatTable];
    setResultCatTable(dataSource2.filter((item) => item.id !== id));
  };

  const handleChangeCat = (event) => {
    setCatName(event.target.value);
  };

  const handleOkAddCat = () => {
    const count2 = countCat + 1;
    setResultCatTable((result) => [
      ...result,
      {
        CatName: CatName,
        key: count2,
        id: count2,
      },
    ]);
    setCountCat(count2);
    // console.log(count);
    setVisible2(false);
  };
  const columns = [
    {
      title: '#',
      dataIndex: 'id',
      width: '5%',
      // editable: true,
    },

    {
      title: 'Categories Name',
      dataIndex: 'CatName',
    },

    {
      title: '',
      dataIndex: 'operation',
      width: '20%',
      render: (_, record) => (
        <Space size="large" style={{ cursor: 'pointer', paddingLeft: `63px` }}>
          <img src={edit} alt="edit" />
          <Popconfirm
            title="Sure to delete?"
            onConfirm={() => handleDeleteCat(record.key)}
          >
            <img src={deletee} alt="delete" />
          </Popconfirm>
        </Space>
      ),
    },
  ];
  return (
    <>
      <Options JobLink="Job categories" />
      <PopUpButton
        title="Create new categories"
        Name="Categories name"
        SkillName={props.SkillName}
        handleChangeSkills={props.handleChangeSkills}
        handleCancelAddJob={props.handleCancelAddJob}
        showModalAddJob={props.showModalAddJob}
        visible2={visible2}
        handleOkAddCat={handleOkAddCat}
        handleChangeCat={handleChangeCat}
        CatName={props.CatName}
      />
      <MainDropdown onChangeS={props.onChangeS} />
      <EditableTable
        handleDelete={props.handleDelete}
        // onChangeS={props.onChangeS}
        result={props.result}
        columns={columns}
      />
    </>
  );
}

export default JobCat;
