import React, { useContext, useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import { Table, Input, Button, Popconfirm, Form, Space, Select } from 'antd';
import './style-table.css';
import edit from '../../images/edit.svg';
import deletee from '../../images/delete.svg';
import { Link } from 'react-router-dom';

const { Option } = Select;

class EditableTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dataSource: [
        {
          key: '0',
          id: '0',
          title: '32',
          location: this.props.location1,
          Sdate: '5-10',
          Edate: '9-10',
          status: 'yes',
        },
        {
          key: '1',
          id: ' 1',
          title: '32',
          location: this.props.location2,
          Sdate: '5-10',
          Edate: '9-10',
          status: 'yes',
        },
      ],
      count: 2,
      input: '',
      result: '',
    };
  }

  componentDidMount() {
    this.setState({
      result: this.state.dataSource,
    });
  }

  onChangeS = (value) => {
    // console.log(`selected ${value}`);

    const dataSource = [...this.state.dataSource];
    const result = !value
      ? dataSource
      : dataSource.filter((item) => item.location.includes(value));

    if (value === 'all') {
      this.setState({
        result: dataSource,
      });
    } else {
      this.setState({
        input: value,
        result: result,
      });
    }
    console.log(result);
  };

  handleDelete = (key) => {
    const dataSource2 = [...this.state.result];
    this.setState({
      result: dataSource2.filter((item) => item.key !== key),
    });
  };

  render() {
    const { dataSource, result } = this.state;
    const {
      jobTitleT,
      jobTitle,
      jobLocation,
      jobLocationT,
      startDate,
      endDate,
      startDateT,
      endDateT,
      statusT,
      status,
    } = this.props;

    this.columns = [
      {
        title: '#',
        dataIndex: 'id',
        width: '5%',
        // editable: true,
      },
      {
        title: jobTitleT,
        dataIndex: jobTitle,
      },
      {
        title: jobLocationT,
        dataIndex: jobLocation,
      },
      {
        title: startDateT,
        dataIndex: startDate,
      },
      {
        title: endDateT,
        dataIndex: endDate,
      },
      {
        title: statusT,
        dataIndex: status,
      },
      {
        title: '',
        dataIndex: 'operation',
        width: '20%',
        render: (_, record) => (
          <Space size="middle" style={{ cursor: 'pointer' }}>
            {/* <a>Edit</a> */}
            <img src={edit} alt="edit" />
            <Popconfirm
              title="Sure to delete?"
              onConfirm={() => this.handleDelete(record.key)}
            >
              {/* <a>Delete</a> */}
              <img src={deletee} alt="delete" />
            </Popconfirm>
          </Space>
        ),
      },
    ];

    const columns = this.columns.map((col) => {
      return {
        ...col,
        onCell: (record) => ({
          record,
          // editable: col.editable,
          dataIndex: col.dataIndex,
          title: col.title,
        }),
      };
    });
    return (
      <div>
        <div className="selectJob">
          <div className="btn-group dropright">
            <span
              className="dropdown-toggle"
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
              style={{
                font: `normal normal bold 23px/42px Lato`,
                color: `#576575`,
                opacity: `1`,
                cursor: `pointer`,
              }}
            >
              Jobs
            </span>

            <div
              className="dropdown-menu"
              style={{
                top: `9px`,
                borderRadius: '13px',
                background: '#519efc',
              }}
            >
              <Link to="/JobCategories" className="dropdown-item">
                Job categories
              </Link>
              <div class="dropdown-divider"></div>
              <Link to="/JobLocations" className="dropdown-item">
                Job locations
              </Link>
              <div class="dropdown-divider"></div>
              <a className="dropdown-item">Job applications</a>
              <div class="dropdown-divider"></div>
              <a className="dropdown-item">Job on board</a>
              <div class="dropdown-divider"></div>
              <Link to="/Skills" className="dropdown-item">
                Skills
              </Link>
            </div>
            <label style={{ marginTop: '35px', marginLeft: '-53%' }}>
              {this.props.JobLink}
            </label>
          </div>
        </div>

        <Link to="/NewJob">
          <Button
            onClick={this.handleAdd}
            type="primary"
            style={{
              marginBottom: 16,
              background: `#509EFC 0% 0% no-repeat padding-box`,
              borderRadius: `6px`,
              opacity: `1`,
              float: `right`,
              marginTop: `107px`,
              marginRight: `10%`,
            }}
          >
            CREATE NEW
          </Button>
        </Link>

        <Select
          showSearch
          style={{
            width: `15%`,
            float: `right`,
            marginRight: `1%`,
            marginTop: `106px`,
          }}
          placeholder="FILTTER STATUS : ALL "
          optionFilterProp="children"
          onChange={this.onChangeS}
          filterOption={(input, option) =>
            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
          }
        >
          <Option value="all">All</Option>
          <Option value="New York">New York</Option>
          <Option value="London">London</Option>
        </Select>

        <Table
          // components={components}
          rowClassName={() => 'editable-row'}
          dataSource={result}
          columns={columns}
          //   onChange={onChange}
        />
      </div>
    );
  }
}

export default EditableTable;
