import { Input, Modal, Col, Row } from 'antd';
import React, { useState, useEffect } from 'react';

function Modall(props) {
  return (
    <>
       <Modal
        title="Filter By"
        visible={props.ModalVisible}
        onOk={props.handleOk}
        onCancel={props.handleCancel}
      >
        <Row gutter={[0, 16]}>
          <Col
            xs={{ span: 18, offset: 5 }}
            sm={{ span: 9, offset: 5 }}
            md={{ span: 10, offset: 5 }}
            lg={{ span: 7, offset: 1 }}
          >
            <Input type="text " placeholder="Show result from" />
          </Col>

          <Col
            xs={{ span: 18, offset: 5 }}
            sm={{ span: 9, offset: 5 }}
            md={{ span: 10, offset: 5 }}
            lg={{ span: 7, offset: 1 }}
          >
            <Input type="text " placeholder="Show result to" />
          </Col>

          <Col
            xs={{ span: 18, offset: 5 }}
            sm={{ span: 9, offset: 5 }}
            md={{ span: 10, offset: 5 }}
            lg={{ span: 7, offset: 1 }}
          >
            <Select
              showSearch
              id={'select'}
              key="status"
              style={{
                width: `100%`,
              }}
              placeholder="All status "
              onChange={props.onChangeStatus}
            >
              <Option value="all">All</Option>
              <Option value="yes" key="status">
                Yes
              </Option>
              <Option value="Yes">Yes</Option>
              <Option value="no">No</Option>
            </Select>
          </Col>
          <Col
            xs={{ span: 18, offset: 5 }}
            sm={{ span: 9, offset: 5 }}
            md={{ span: 10, offset: 5 }}
            lg={{ span: 7, offset: 1 }}
          >
            <Select
              style={{
                width: `100%`,
              }}
              placeholder="All locations "
              onChange={props.onChangeLocation}
            >
              <Option value="all">All</Option>
              <Option value="New York">New York</Option>
              <Option value="London">London</Option>
            </Select>
          </Col>

          <Col
            xs={{ span: 18, offset: 5 }}
            sm={{ span: 9, offset: 5 }}
            md={{ span: 10, offset: 5 }}
            lg={{ span: 7, offset: 1 }}
          >
            <Select
              showSearch
              style={{
                width: `100%`,
              }}
              placeholder="skills "
              optionFilterProp="children"
              onChange={props.onChangeS}
              filterOption={(input, option) =>
                option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
              }
            >
              <Option value="all">All</Option>
              <Option value="New York">yes</Option>
              <Option value="London">no</Option>
            </Select>
          </Col>

          <Col
            xs={{ span: 18, offset: 5 }}
            sm={{ span: 9, offset: 5 }}
            md={{ span: 10, offset: 5 }}
            lg={{ span: 7, offset: 1 }}
          >
            <Select
              showSearch
              style={{
                width: `100%`,
              }}
              placeholder="All job "
              optionFilterProp="children"
              onChange={props.onChangeS}
              filterOption={(input, option) =>
                option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
              }
            >
              <Option value="all">All</Option>
              <Option value="New York">yes</Option>
              <Option value="London">no</Option>
            </Select>
          </Col>
        </Row>
      </Modal>
      )}
    </>
  );
}

export default Modal;
