import { Form, Input, Col, Row, Button } from 'antd';

function ApplyForm(props) {
  const layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };
  /* eslint-disable no-template-curly-in-string */

  const validateMessages = {
    required: '${label} is required!',
    types: {
      email: '${label} is not a valid email!',
      number: '${label} is not a valid number!',
    },
    number: {
      range: '${label} must be between ${min} and ${max}',
    },
  };
  const onFinish = (values) => {
    console.log(values);
  };
  return (
    <>
      <Form
        {...layout}
        name="nest-messages"
        onFinish={onFinish}
        validateMessages={validateMessages}
      >
        <Row gutter={[0, 8]} style={{ paddingTop: `110px` }}>
          <Col
            xs={{ span: 20, offset: 1 }}
            sm={{ span: 22, offset: 2 }}
            md={{ span: 20, offset: 3 }}
            lg={{ span: 14, offset: 5 }}
          >
            <Form.Item name={['user', 'name']} label="Full Name:">
              <Input />
            </Form.Item>
          </Col>
          <Col
            xs={{ span: 20, offset: 1 }}
            sm={{ span: 22, offset: 2 }}
            md={{ span: 20, offset: 3 }}
            lg={{ span: 14, offset: 5 }}
          >
            <Form.Item name={['user', 'age']} label="Date of birth:">
              <Input />
            </Form.Item>
          </Col>
          <Col
            xs={{ span: 20, offset: 1 }}
            sm={{ span: 22, offset: 2 }}
            md={{ span: 20, offset: 3 }}
            lg={{ span: 14, offset: 5 }}
          >
            <Form.Item name={['user', 'website']} label="Location:">
              <Input />
            </Form.Item>
          </Col>
          <Col
            xs={{ span: 20, offset: 1 }}
            sm={{ span: 22, offset: 2 }}
            md={{ span: 20, offset: 3 }}
            lg={{ span: 14, offset: 5 }}
          >
            <Form.Item name={['user', 'website']} label="Upload CV:">
              <Input />
            </Form.Item>
          </Col>
          <Col
            xs={{ span: 20, offset: 1 }}
            sm={{ span: 22, offset: 2 }}
            md={{ span: 20, offset: 3 }}
            lg={{ span: 14, offset: 5 }}
          >
            <Form.Item name={['user', 'introduction']} label="More info:">
              <Input.TextArea />
            </Form.Item>
          </Col>
          <Col
            xs={{ span: 20, offset: 1 }}
            sm={{ span: 22, offset: 2 }}
            md={{ span: 20, offset: 3 }}
            lg={{ span: 14, offset: 5 }}
          >
        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
              </Col>
        </Row>
      </Form>
    </>
  );
}

export default ApplyForm;
